# <details>
#     <summary> 
#         <strong style = "cursor: pointer; background-color: rgba(0, 0, 0, 0.2); color: #000000; font-family: Franklin Gothic Heavy; padding: 8px; border-radius: 5px; display: inline-block;"> 
